import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { HouseService } from './houses.service';

@Component({
  selector: "app-houses-houseform",
  templateUrl: "./houseform.component.html",
  styleUrls: ['./houseform.component.css']
})

export class HouseformComponent {
  houseForm: FormGroup;
  isEditMode: boolean = false;
  houseId: number = -1;
  gridImg: File | null = null;
  moreImg: File[] | null = null;

  constructor(private _formbuilder: FormBuilder,
    private _router: Router,
    private _houseService: HouseService,
    private _route: ActivatedRoute) {
    this.houseForm = _formbuilder.group({
      address: ['', Validators.required],
      price: [null, [Validators.required, Validators.pattern(/^\d+(\.\d{1,2})?$/)]],
      imageUrl: [''],
      bedrooms: [null, [Validators.required, Validators.pattern(/^\d+(\.\d{1,2})?$/)]],
      guests: [null, [Validators.required, Validators.pattern(/^\d+(\.\d{1,2})?$/)]],
      description: [''],
      userId: [0],
      gridImg: [null],
      moreImg: [[null]]
    });
  }

  onFileSelected(event: any): void {
    const fileInput = event.target;
    if (fileInput.files.length > 0) {
      const imgUrl = fileInput.files[0].name;
      this.houseForm.patchValue({ imageUrl: imgUrl });
      this.houseForm.patchValue({ gridImg: fileInput.files[0] });
    }
  }

  onMoreImgSelected(event: any): void {
    const moreImgs = event.target;
    if (moreImgs.files.length > 0) {
      this.houseForm.patchValue({ moreImg: moreImgs.files });
    }
  }

  onSubmit() {
    const newHouse = this.houseForm.value;

    const gridImgControl = this.houseForm.get('gridImg');
    const addressControl = this.houseForm.get('address');
    const moreImgControl = this.houseForm.get('moreImg');

    if (addressControl && addressControl.value) {
      const address = addressControl.value;

      if ((gridImgControl && gridImgControl.value)) {
        const gridImg = gridImgControl.value;

        this._houseService.createDirGridImg(gridImg, address).subscribe(dirResponse => {
          if (dirResponse.success) {
            console.log(dirResponse.message);
          } else {
            console.error('Could not create directory');
          }
        });
      }

      if (moreImgControl && moreImgControl.value) {
        const images = moreImgControl.value;
        this._houseService.uploadImages(images, address).subscribe(imgResponse => {
          if (imgResponse.success) {
            console.log(imgResponse.message);
          } else {
            console.error("Images failed to upload");
          }
        });
      }
    }

    if (this.isEditMode) {
      this._houseService.updateHouse(this.houseId, newHouse)
        .subscribe(response => {
          if (response.success) {
            console.log(response.message);
            this._router.navigate(['/houses']);
          } else {
            console.log('House update failed');
          }
        });
    } else {
      this._houseService.createHouse(newHouse).subscribe(response => {
        if (response.success) {
          console.log(response.message);
          this._router.navigate(['/houses']);
        } else {
          console.log('House creation failed');
        }
      });
    }
  }



  backToHouses() {
    this._router.navigate(['/houses']);
  }

  ngOnInit(): void {
    this._route.params.subscribe(params => {
      if (params['mode'] === 'create') {
        this.isEditMode = false;
      } else if (params['mode'] === 'edit') {
        this.isEditMode = true;
        this.houseId = +params['id'];
        this.loadHouseForEdit(this.houseId);
      }
    });

    this._houseService.showId(sessionStorage.getItem("email")!).subscribe(
      (userIdValue: number) => {
        // Update the userId in the form
        this.houseForm.get('userId')!.setValue(userIdValue);
      },
      (error) => {
        console.error("Error fetching userId:", error);
      }
    );
  }

  loadHouseForEdit(houseId: number) {
    this._houseService.getHouseById(houseId)
      .subscribe(
        (house: any) => {
          this.houseForm.patchValue({
            price: house.Price,
            address: house.Address,
            imageUrl: house.ImageUrl,
            bedrooms: house.Bedrooms,
            guests: house.Guests,
            description: house.Description,
            userId: house.user.UserId
          });
        },
        (error: any) => {
          console.error('Error loading house for edit: ', error);
        }
      );
  }

}
<div class="fullArea">
    <div class="container-fluid">
        <h1>{{ isEditMode ? 'Update' : 'Create' }} listing</h1>

        <form [formGroup]="houseForm" (ngSubmit)="onSubmit()">
          <div class="form-group">
            <label for="address">Address <i class="bi bi-cash-coin"></i></label>
            <input id="address" class="form-control" formControlName="address" />


            <div *ngIf="houseForm.get('address')?.invalid && houseForm.get('address')?.touched" class="text-danger">
              Address cannot be empty
            </div>

          </div>
            <div class="form-group" [class.has-error]="houseForm.get('price')?.invalid && houseForm.get('price')?.touched">
                <label for="price">Price <i class="bi bi-cash-stack"></i></label>
                <input id="price" class="form-control" formControlName="price" />


               <div *ngIf="houseForm.get('price')?.invalid && (houseForm.get('price')?.touched && houseForm.get('price')?.dirty)" class="text-danger">
                    Please enter a valid numeric value for price.
               </div>

            </div>
            <div class="form-group" [class.has-error]="houseForm.get('bedrooms')?.invalid && houseForm.get('bedrooms')?.touched">
                <label for="bedrooms">Bedrooms <i class="bi bi-door-open"></i></label>
                <input id="bedrooms" class="form-control" formControlName="bedrooms" />

                <div *ngIf="houseForm.get('bedrooms')?.invalid && (houseForm.get('bedrooms')?.touched && houseForm.get('bedrooms')?.dirty)" class="text-danger">
                    Please enter a valid numeric value for bedrooms.
                </div>

            </div>

            <div class="form-group">
                <label for="gridImg">Grid image <i class="bi bi-card-image"></i></label>
                <input type="file" id="gridImg" class="form-control" formControlName="gridImg" accept="image/*" (change)="onFileSelected($event)" />
            </div>

            <div class="form-group" *ngIf="isEditMode || houseForm.get('gridImg')?.value">
                <label for="moreImg">More images? <i class="bi bi-images"></i></label>
                <input type="file" multiple id="moreImg" class="form-control" formControlName="moreImg" accept="image/*" (change)="onMoreImgSelected($event)" />
            </div>

            <div class="form-group" [class.has-error]="houseForm.get('guests')?.invalid && houseForm.get('guests')?.touched">
                <label for="guests">Guests <i class="bi bi-people-fill"></i></label>
                <input id="guests" class="form-control" formControlName="guests" />
                <div *ngIf="houseForm.get('guests')?.invalid && (houseForm.get('guests')?.touched && houseForm.get('guests')?.dirty)" class="text-danger">
                    Please enter a valid numeric value for guests.
                </div>
            </div>

            <div class="form-group">
                <label for="description">Description <i class="bi bi-text-left"></i></label>
                <input id="description" class="form-control" maxlength="200" formControlName="description" />
            </div>
            <div class="form-group" style="display: none;">
                <label for="userId">UserId <i class="bi bi-cash-coin"></i></label>
                <input id="userId" class="form-control" formControlName="userId" />
            </div>
            <button id="save" class="btn btn-primary" type="submit" [disabled]="houseForm.invalid">{{ isEditMode ? 'Update' : 'Create' }} listing</button>
            <button id="cancel" class="btn btn-secondary" [routerLinkActive]="['link-active']" [routerLink]="['/listings']">Cancel</button>
        </form>
    </div>
</div>
