import { Component, OnInit } from '@angular/core';
import { IHouse } from '../house';
import { Router } from '@angular/router';
import { HouseService } from '../houses.service';

@Component({
  selector: 'app-details', // Specify the selector for the component
  templateUrl: './details.component.html', // Path to the HTML template
  styleUrls: ['./details.component.css'] // Array of CSS stylesheets
})

export class DetailsComponent implements OnInit {
  house: IHouse = {} as IHouse; // Adjust the type according to your house object structure

  constructor(private _router: Router,
    private _houseService: HouseService,
    private _route: ActivatedRoute) {}

  ngOnInit(): void {
    this.getHouse();
  }

  getHouse(): void {
    this._houseService.getHouseById(1).subscribe(
      (data: any) => {
        this.house = data;
        console.log(JSON.stringify(this.house));
      },
      (error: any) => {
        console.error('Error fetching house:', error);
        // Handle error if necessary
      }
    );
  }
}
