import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { UserService } from '../users.service';

@Component({
  selector: 'app-register-component',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})

export class RegisterComponent {
  userForm: FormGroup;

  constructor(private _formbuilder: FormBuilder,
    private _router: Router,
    private _userService: UserService,
    private _route: ActivatedRoute) {
    this.userForm = _formbuilder.group({
      firstName: ['', Validators.required],
      lastName: ['', Validators.required],
      address: ['', Validators.required],
      number: ['', Validators.required],
      email: ['', Validators.required],
      password: ['', Validators.required]
    });
    }

  ngOnInit(): void {
    console.log("register");
  }

  onSubmit() {
    const newUser = this.userForm.value;
    //newUser.email = newUser.email.toLowerCase();

    this._userService.createUser(newUser).subscribe(response => {
      if (response.success) {
        console.log(response.message);
        this._router.navigate(['/login']);
      } else {
        console.log('User creation failed');
      }
    });
  }
}
<div class="container">
    <h1>
        Register
    </h1>
    <form class="row" [formGroup]="userForm" (ngSubmit)="onSubmit()">
        <div class="col-md-6">
            <div class="form-group">
                <label for="firstName"><b>First name</b><i class="bi bi-cash-coin"></i></label>
                <input id="firstName" class="form-control" formControlName="firstName" />
            </div>
        </div>
        <div class="col-md-6">
            <div class="form-group">
                <label for="lastName"><b>Last name</b><i class="bi bi-cash-coin"></i></label>
                <input id="lastName" class="form-control" formControlName="lastName" />
            </div>
        </div>
        <div class="col-md-12">
            <div class="form-group">
                <label for="address"><b>Address</b><i class="bi bi-cash-coin"></i></label>
                <input id="address" class="form-control" formControlName="address" />
            </div>
        </div>
        <div class="col-md-12">
            <div class="form-group">
                <label for="number"><b>Number</b><i class="bi bi-cash-coin"></i></label>
                <input id="number" class="form-control" formControlName="number" />
            </div>
        </div>
        <div class="col-md-12">
            <div class="form-group">
                <label for="email"><b>Email</b><i class="bi bi-cash-coin"></i></label>
                <input id="email" class="form-control" formControlName="email" />
            </div>
        </div>
        <div class="form-group">
            <label for="password"><b>Password</b><i class="bi bi-cash-coin"></i></label>
            <input type="password" id="password" class="form-control" formControlName="password" />
        </div>
    </form>
    <button id="register" type="submit">Register</button>
</div>
