import { Component } from '@angular/core';
import { FormGroup, FormControl, Validators, FormBuilder, ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: "app-houses-houseform",
  templateUrl: "./houseform.component.html"
})

export class HouseformComponent {
  houseForm: FormGroup;

  constructor(private _formbuilder: FormBuilder, private _router: Router, private _http: HttpClient) {
    this.houseForm = _formbuilder.group({
      address: ['', Validators.required],
      price: [0, Validators.required],
      imageUrl: [''],
      bedrooms: [0],
      guests: [0],
      description: [''],
      userId: [0]
    });
  }

  onSubmit() {
    console.log("HouseCreate form submitted");
    console.log(this.houseForm);
    const newHouse = this.houseForm.value;
    const createUrl = "api/house/create";
    this._http.post<any>(createUrl, newHouse).subscribe(response => {
      if (response.success) {
        console.log(response.message);
        this._router.navigate(['/houses']);
      }
      else {
        console.log('House creation failed');
      }
    });
  }

  backToHouses() {
    this._router.navigate(['/houses']);
  }
}
<h2>Create house</h2>

<form [formGroup]="houseForm" (ngSubmit)="onSubmit()">
  <div class="form-group">
    <label asp-for="Price">Price <i class="bi bi-cash-coin"></i></label>
    <input asp-for="Price" class="form-control" />
  </div>
  <div class="form-group">
    <label asp-for="Address">Address <i class="bi bi-cash-coin"></i></label>
    <input asp-for="Address" class="form-control" />
  </div>
  <div class="form-group">
    <label asp-for="Bedrooms">Bedrooms <i class="bi bi-door-open"></i></label>
    <input asp-for="Bedrooms" class="form-control" />

  </div>
  <div class="form-group">
    <label asp-for="ImageUrl">ImageUrl <i class="bi bi-door-open"></i></label>
    <input asp-for="ImageUrl" class="form-control" />
  </div>
    <div class="form-group">
      <label asp-for="Guests">Guests <i class="bi bi-people-fill"></i></label>
      <input asp-for="Guests" class="form-control" />
    </div>
    <div class="form-group">
      <label asp-for="Description">Description <i class="bi bi-text-left"></i></label>
      <input asp-for="Description" class="form-control" maxlength="200" />
    </div>
    <div class="form-group">
      <label asp-for="UserId">UserId <i class="bi bi-cash-coin"></i></label>
      <input asp-for="UserId" class="form-control" />
    </div>
    <button class="btn btn-primary" type="submit" [disabled]="houseForm.invalid">Create house</button>
    <button class="btn btn-secondary" (click)="backToHouses()">Cancel</button>
</form>
